const init = { items: [] };

export const itemsReducer = (state = init, action) => {
  switch (action.type) {
    case 'LOAD_ITEMS': {
      const mix = action.payload.sort(() => Math.random - 0.5);
      console.log(mix);
      return { ...state, items: mix };
    }
    default: {
      return { ...state };
    }
  }
};
