const init = { cart: [] };

export const cartReducer = (state = init, action) => {
  switch (action.type) {
    case 'ADD_TO_CART': {
      console.log(action.paylaod);
      const updatedCart = [...state.cart, action.paylaod];
      return { ...state, cart: updatedCart };
    }
    default: {
      return { ...state };
    }
  }
};
