import { Button, TextField } from '@mui/material';
import React from 'react';
import { useSelector } from 'react-redux';
import './Cart.scss';
import { Link } from 'react-router-dom';
import CartItem from '../CartItem/CartItem';
export default function Cart() {
  const cartItems = useSelector((state) => state.cart.cartItems);
  let total;
  cartItems.forEach((elem) => {
    console.log(elem.price);
  });

  return (
    <div className="cart-content">
      <h1 className="title">My Shopping Cart</h1>
      <div className="cart-text">
        <p className="description">Description</p>
        <p className="quantity">Quantity</p>
        <p className="price">Price</p>
      </div>
      {cartItems.length === 0 ? (
        <div className="empty-cart-content">
          <img
            className="cart-img"
            src="https://www.valeorx.com/static/media/empty-cart.60e68bfd.png"
            alt="cart img"></img>
          <Link style={{ textDecoration: 'none' }} to="/">
            <Button className="btn" variant="contained">
              Look for items
            </Button>
          </Link>
        </div>
      ) : (
        cartItems.map((item) => (
          <CartItem
            className="cart-item"
            title={item.title}
            description={item.description}
            price={item.price}
            img={item.img}
            rating={item.rating}
            quantity={item.quantity}
          />
        ))
      )}
      <div className="cart-footer">
        <div className="discount-block">
          <TextField id="outlined-basic" label="Coupon" variant="outlined" />
          <Button style={{ marginLeft: 10 }} variant="outlined">
            Enter
          </Button>
        </div>
        <p>discount</p>
        <p>{total}</p>
      </div>
    </div>
  );
}
